mapper.select:
select *
from lsupporter
where wid = #{wid}

select count(*)
from fieldstaff
where email = #{email} and name = #{name}

select l.wid
from fieldstaff f, lsupporter l
where f.email = #{email}

select count(*)
from fieldstaff f, lsupporter l
where f.name = #{name} and f.email = #{email} and l.wid = #{wid}

select count(*)
from member m, lsupporter l, emergency e
where m.wid = l.wid and e.id = m.id and l.wid = #{wid}

select e.name, e.phone,e.relation
from member m, ecall e, lsupporter l
where m.id = e.id and m.id = #{id} and l.wid = m.wid and l.wid= #{wid}

select count(e.id)
from member m, emergency e, lsupporter l
where m.wid = l.wid and e.scode='1' and l.wid= #{wid} and m.id= #{id}

select count(e.id)
from member m, emergency e, lsupporter l
where m.wid = l.wid and e.scode='3' and l.wid= #{wid} and m.id= #{id}

select DISTINCT count(r.rno)
from member m, report r, lsupporter l, fieldstaff fs
where m.wid = l.wid and r.id = m.id and fs.wcode = l.wcode and m.id = #{id}

select *
from report
where rNo = #{rNO}

mapper.update:
update report 
set 
occurTime=#{occurTime},occurType=#{occurType},
callCheck=#{callCheck},content=#{content}
where rNo=#{rNo}

mapper.select:
		select *
		from report r, member m
        where r.id = m.id and r.rno=#{rNo}

select *
from member
where id = #{id}
